import 'regenerator-runtime/runtime';

import { API_URL, KEY, RESULTS_PER_PAGE } from './config.js';

export const state = {
  recipe: {},
  search: {
    query: '',
    results: [],
    resultsPerPage: RESULTS_PER_PAGE,
    page: 1,
  },
  bookmarks: [],
};

export const loadRecipe = async recipeId => {
  try {
    if (!recipeId) return;

    const res = await fetch(`${API_URL}/${recipeId}`);

    const data = await res.json();

    const {
      cooking_time,
      id,
      image_url,
      ingredients,
      publisher,
      servings,
      source_url,
      title,
    } = data.data.recipe;

    state.recipe = {
      id: id,
      title: title,
      publisher: publisher,
      sourceURL: source_url,
      imageURL: image_url,
      servings: servings,
      cookingTime: cooking_time,
      ingredients: ingredients,
    };

    if (!res.ok) throw new Error(`${data.message} (${res.status})`);
  } catch (err) {
    throw err;
  }
};

export const loadRecipeList = async searchTerm => {
  try {
    const res = await fetch(`${API_URL}/?search=${searchTerm}&key=${KEY}`);
    const data = await res.json();

    //set page pagination to page 1
    state.search.page = 1;

    return data.data.recipes;
  } catch (err) {
    console.log(err);
  }
};

export const getSearchResultsPage = (page = state.search.page) => {
  state.search.page = page;

  const start = (page - 1) * state.search.resultsPerPage;
  const end = page * state.search.resultsPerPage;

  return state.search.results.slice(start, end);
};

export const updateServings = newServings => {
  if (newServings === 0) return;

  state.recipe.ingredients.forEach(ing => {
    //1) update quantity
    //newQt = oldQt / oldServings * newServings
    ing.quantity = (+ing.quantity / +state.recipe.servings) * newServings;
  });

  //2) update servings
  state.recipe.servings = newServings;
};

export const toggleBookmark = () => {
  //check if bookmark exist or not
  const bookmarkIndex = state.bookmarks.findIndex(
    bookmark => bookmark.id === state.recipe.id
  );

  //bookmark exist
  //remove bookmark
  if (bookmarkIndex !== -1) {
    state.recipe.bookmarked = false;
    state.bookmarks.splice(bookmarkIndex, 1);
  } else {
    //bookmark don't exist
    //add bookmark
    state.recipe.bookmarked = true;
    state.bookmarks.push(state.recipe);
  }
};

export const saveBookmarksToLocalStorage = bookmarks => {
  localStorage.setItem('recipeBookmarks', JSON.stringify(bookmarks));
};

export const getBookmarksFromLocalStorage = () => {
  return JSON.parse(localStorage.getItem('recipeBookmarks'));
};
